<template>
        <el-row style="margin:auto;width: 1100px; margin:5px;">
            <img src="data:image/jpeg;base64," alt="" width="50">
            <span style="font-size:40px;padding: 10px;">‰ªäÊó•ÊÉÖÁª™ÂàÜÊûê</span>
        </el-row>
        <div class="echarts-box" style="display:flex; justify-content: center; flex-wrap: wrap;width: 1100px; margin:5px;">
            <div id="pieEcharts" :style="{ width: '900px', height: '300px' }"></div>
            <div id="barEcharts" :style="{ width: '900px', height: '300px' }"></div>
        </div>
</template>

<script>
import * as echarts from "echarts";
import { ref, onMounted, onUnmounted } from "vue";
import { http } from '../untils/request.js'
export default {
    props: ['data',],
    emits: ['submit'],
    setup(props, { emit }) {
        const sentiment = ref(props.data.sentiment);
        let value = {
            postive: 0,
            nagetive: 0
        };
        let data = {
            index: [],
            val: []
        };

        const getData = async function () {
            try {
                const response = await http.get('sentiments');
                emit('submit', 'sentiment', response.data);
                sentiment.value = response.data;
                value = count(sentiment.value);
                data = {
                    index: range(1, sentiment.value.length + 1),
                    val: sentiment.value
                }
                console.log(value)
                console.log(data)
            } catch (error) {
                console.error(error);
            }
        }
        let echart = echarts;
        onMounted(async () => {
            if (sentiment.value == null) {
                await getData();
            }
            else {
                value = count(sentiment.value);
                data = {
                    index: range(1, sentiment.value.length + 1),
                    val: sentiment.value
                }
            }
            initChart(value, data);
        });

        onUnmounted(() => {
            echart.dispose;
        });

        function count(data) {
            if (data == null) {
                return;
            }
            let count = 0;
            for (let i of data) {
                count += i < 0.4 ? 0 : 1
            }
            return {
                postive: count,
                nagetive: data.length - count
            }
        }
        
        function range(start, end) {
            return [...Array(end - start).keys()].map(item => start + item);
        }

      function ave(arr) {
        let sum = 0;
        for (let i of arr) {
          sum += i;
        }
        return sum / arr.length
      }

        function initChart(value, data) {
            let pieChart = echart.init(document.getElementById("pieEcharts"), "white");
            let barChart = echart.init(document.getElementById("barEcharts"), "white");
            pieChart.setOption({
                tooltip: {
                    trigger: 'item'
                },
                legend: {
                    top: '5%',
                    left: 'center'
                },
                series: [
                    {
                        name: 'Access From',
                        type: 'pie',
                        radius: ['40%', '70%'],
                        avoidLabelOverlap: false,
                        itemStyle: {
                            borderRadius: 10,
                            borderColor: '#fff',
                            borderWidth: 2
                        },
                        label: {
                            show: false,
                            position: 'center'
                        },
                        emphasis: {
                            label: {
                                show: true,
                                fontSize: '40',
                                fontWeight: 'bold'
                            }
                        },
                        labelLine: {
                            show: false
                        },
                        data: [
                            { value: value.postive, name: 'Postive üòä' },
                            { value: value.nagetive, name: 'Negative üò°' },
                        ]
                    }
                ]
            });
            let average = ave(data.val)
            barChart.setOption({
                title: {
                    text: 'ÁÉ≠ÊêúÊÉÖÁª™ÊåáÊï∞',
                    left: 10
                },
                toolbox: {
                    feature: {
                        dataZoom: {
                            yAxisIndex: false
                        },
                        saveAsImage: {
                            pixelRatio: 2
                        }
                    }
                },
                tooltip: {
                    trigger: 'axis',
                    axisPointer: {
                        type: 'shadow'
                    }
                },
                grid: {
                    bottom: 90
                },
                dataZoom: [
                    {
                        type: 'inside'
                    },
                    {
                        type: 'slider'
                    }
                ],
                xAxis: {
                    show: false,
                    data: data.index,
                    silent: false,
                    splitLine: {
                        show: false
                    },
                    splitArea: {
                        show: false
                    }
                },
                yAxis: {
                    splitArea: {
                        show: false
                    }
                },
                series: [
                    {
                        type: 'bar',
                        data: data.val,
                        large: true
                    },
                  {
                    type: 'line',
                    markLine: {
                      symbol: ['none', 'none'],
                      silent: true,
                      lineStyle: {
                        normal: {
                          color: 'red',
                          width: 2,
                        }
                      },
                      data: [{
                        yAxis: average,
                      }],
                      label: {
                        position: 'top',
                        normal: {
                          formatter: average.toFixed(4)
                        }
                      },
                    },
                  }
                ]
            });
            window.onresize = function () {
                pieChart.resize();
                barChart.resize();
            };
        }
        return { initChart };
    }
};
</script>